# function qui va donner la longueur de l'element
def f(str)
    str.length
 end
 # array to hash 
 h = Hash[ *twitter_logs.collect { |v| [ v, f(v) ] }.flatten ]
 
 # le nombre de fois où le même l'element revient 
 l =  h.values
  print "\n \n8) "
  print l.uniq.map { |x| "#{x} revient #{l.count(x)} fois \n" }.join(' ')
 
